import React from "react";
import Header from "../../components/header/Header";
import Greeting from "../../containers/greeting/Greeting";
import IntroText from "../../containers/greeting/IntroText";
import Footer from "../../components/footer/Footer";
import SocialMedia from "../../components/socialMedia/SocialMedia";
import Updates from "../../containers/updates/Updates";
import NewEventCard from "../newevent/newEvent";
import { Fade } from "react-reveal";
function Home(props) {
  React.useEffect(() => {
    if(props.anime==true) {
      setTimeout(() =>{props.setAnime(false)},8000)
    }
  }, [[props.anime]])
  return (
    <div>
      <Header theme={props.theme} setTheme={props.setTheme} anime={props.anime} setAnime={props.setAnime} />
      {(props.anime===true)?(
      <div id="fly-in" style={{height:'20vh',marginTop:'25vh'}} >
            <div>GET⠀‎‎READY</div>
            <div>To⠀‎‎witness</div>
            <div>The⠀world's largest patriotic ‎‎fest⠀‎‎ever</div>
            <div>Register Now!</div>
        </div>
        ):(
          <></>
        )}
        <NewEventCard />
      <Greeting theme={props.theme}  anime={props.anime} setAnime={props.setAnime} />
      <Fade bottom duration={2000} delay={(props.anime==true)?8000:0} distance="40px">
      <SocialMedia />
      </Fade>
      <Footer theme={props.theme} />
    </div>
  );
}

export default Home;
