import React, { useState } from "react"
import "./register.css"
import axios from "axios"
import { useNavigate} from "react-router"

const Register = () => {

    const history = useNavigate()

    const [ user, setUser] = useState({
        Name: "",
        Email:"",
        Password:"",
        ReEnterPassword: "",
        Phonenumber: "",
        Gender:""
    })

    const handleChange = e => {
        const { name, value } = e.target
        setUser({
            ...user,
            [name]: value
        })
    }

    const register = () => {
        const { Name, Email, Password, ReEnterPassword } = user
        if( Name && Email && Password && (Password == ReEnterPassword)){
            axios.post("http://localhost:3001/register", user)
            .then( res => {
                alert(res.data.message)
                history.push("/login")
            })
        } else {
            alert("invalid input")
        }
        
    }

    return (
        <center>
        <div className="register">
            {console.log("User", user)}
            <h1>Register </h1>
            <input type="text" name="Name" value={user.Name} placeholder="Your Name" onChange={ handleChange }></input>
            <input type="text" name="Email" value={user.Email} placeholder="Your Email" onChange={ handleChange }></input>
            <input type="password" name="Password" value={user.Password} placeholder="Your Password" onChange={ handleChange }></input>
            <input type="password" name="ReEnterPassword" value={user.ReEnterPassword} placeholder="Re-enter Password" onChange={ handleChange }></input>
            <input type="text" name="Phonenumber" value={user.Phonenumber} placeholder="Phone Number" onChange={ handleChange }></input>
            <input type="text" name="Gender" value={user.Gender} placeholder="Gender" onChange={ handleChange }></input>
            <div className="button" href="/#Login" onClick={register} >Register</div>
            <div>or</div>
            <div className="button" onClick={() => history.push("/login")}>Login</div>
        </div>
        </center>
    )
}

export default Register
